by_created.map:
function (doc, meta) {
  if (doc.doctype == "post") {
    var mostRecentVersion = doc.versions[doc.versions.length -1];
    emit(doc.created, {
      "title": doc.title,
      "byline": doc.byline,
      "author": doc.author,
      "created": doc.created,
      "tags": doc.tags,
      "slug": doc.slug,
      "latest": mostRecentVersion
    });
  }
}

